mapper.update:
UPDATE user_tb SET username = #{user.username},level = #{user.level}
,password = #{user.password}
WHERE id = #{user.id};

mapper.select:
SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM person_tb p left join user_tb u on p.user_id = u.id WHERE 1=1
and p.name like CONCAT('%',#{keyword},'%')
and u.username like CONCAT('%',#{keyword},'%')
) ost;
